.autocomplete-text {
  background-color: transparent;
  border: none;
  color: white;
  font-weight: 900;
  font-size: 110%;
}
.autocomplete-dropdown {
  color: white;
  font-size: 90%;
  font-family: "Lucida Console";
  background-color: black;
  font-weight: normal;
  border-radius: 5px;
}
.autocomplete-hover-item {
  background-color: #626262;
  color: black;
  font-weight: normal;
}
.autocomplete-hint {
  color: gray;
}
.atp-panel {
  /*
  Atom-terminal-panel
  Copyright by isis97
  MIT licensed

  Terminal basic stylesheet.
  Feel free to edit and change the terminal appearance!
*/
