}
.atp-panel .atp-close-btn {
  position: absolute;
  right: 40px;
  background-color: transparent;
  cursor: pointer;
  border-style: none;
  color: gray;
  font-size: 150%;
}
.atp-panel .atp-close-btn:hover::before {
  color: black;
}
.atp-panel .atp-close-btn:before {
  content: '\25BC';
}
.atp-panel .atp-destroy-btn {
  position: absolute;
  right: 10px;
  background-color: transparent;
  cursor: pointer;
  border-style: none;
  color: gray;
  font-size: 150%;
}
.atp-panel .atp-destroy-btn:hover::before {
  color: black;
}
.atp-panel .atp-destroy-btn:before {
  content: 'âœ–';
}
.atp-panel .atp-maximize-btn {
  position: absolute;
  right: 70px;
  background-color: transparent;
  cursor: pointer;
  border-style: none;
  color: gray;
  font-size: 150%;
}
.atp-panel .atp-maximize-btn:hover::before {
  color: black;
}
.atp-panel .atp-maximize-btn:before {
  content: '\25B2';
}
.atp-panel.panel {
  margin-bottom: 0px;
  white-space: pre;
  word-wrap: break-word;
  font-size: 14px;
  font-family: "Lucida Console";
}
.atp-panel .welcome-panel .panel-heading {
  background-color: #3a3f4b;
}
.atp-panel .panel-heading {
  padding: 2px;
  text-align: right;
}
.atp-panel .panel-heading .btn {
  top: 0px;
}
.atp-panel .panel-heading .button-settings {
  float: left;
}
.atp-panel .terminal {
  background-color: #0c0c0c;
}
.atp-panel .panel-divider {
  background-color: #636363;
  border-top: 5px solid #3B3A35;
  box-shadow: 0 2px 3px 0px #20457C;
}
.atp-panel .terminal {
  max-height: 300px;
  overflow-y: scroll;
  bottom: 10px;
}
.atp-panel .terminal .file-extension {
  /*
  Atom-terminal-panel
  Copyright by isis97
  MIT licensed

  Terminal file icons stylesheet.
  If you want to add the new extension just do it!
  The terminal automatically looks for the LESS class called like the found extension!
  So if you wants to make you '.sh' coloured blue just type:
    .sh {
      color: blue;
    }
  Simple right? :)
*/
  /* Basic colouring for Javascript/Coffeescript/C/C++ */
  /* Basic colouring for input/output sample files */
  /* Basic colouring for C/C++ header files */
  /* And after all some Windows executables... */
}
.atp-panel .terminal .file-extension .parent-folder {
  font-weight: 900;
  color: red;
}
.atp-panel .terminal .file-extension .cpp,
.atp-panel .terminal .file-extension .c,
.atp-panel .terminal .file-extension .js,
.atp-panel .terminal .file-extension .coffe,
.atp-panel .terminal .file-extension .json,
.atp-panel .terminal .file-extension .cson {
  color: #26c26a;
}
.atp-panel .terminal .file-extension .in,
.atp-panel .terminal .file-extension .out {
  color: #40b5bf;
}
.atp-panel .terminal .file-extension .h,
.atp-panel .terminal .file-extension .hpp {
  color: #388a0f;
}
.atp-panel .terminal .file-extension .bat,
.atp-panel .terminal .file-extension .exe {
  color: #696969;
}
.atp-panel .terminal .file-info {
  width: 140px;
  display: inline-block;
  float: left;
}
.atp-panel .terminal .file-not-found {
  text-decoration: line-through;
  color: #A41629;
  cursor: not-allowed;
}
.atp-panel .terminal .console-link {
  cursor: pointer;
}
.atp-panel .terminal .console-link:hover {
  text-decoration: underline;
}
.atp-panel .terminal .console-link.link-used {
  font-weight: bold;
  color: #8B3E2F;
}
.atp-panel .terminal .stat-program {
  color: #26c26a;
}
.atp-panel .terminal .stat-link {
  color: #cd7de8;
}
.atp-panel .terminal .clear {
  clear: both;
}
.atp-panel .status-ignored {
  opacity: 0.5;
}
.atp-panel .status-running {
  color: #00ffff;
}
.atp-panel .status-info {
  color: #0055ff;
}
.atp-panel .status-success {
  color: #55ff00;
}
.atp-panel .status-error {
  color: #ff0000;
}
.atp-panel .icon-terminal,
.atp-panel .icon-plus {
  cursor: pointer;
}
